<?phpif (!isset($params["name"])) $params["name"] = "text";if (!isset($params["value"])) $params["value"] = "";if (!isset($params["placeholder"])) $params["placeholder"] = "";$EleId = "CKEditor".rand(0,999999);?><script src="<?=$_ENV["baseurl"] ?>skins/default/lib/ckeditor/ckeditor.js"></script><TEXTAREA class="ckeditor2" id="<?=$EleId ?>" name="<?=$params["name"]; ?>" placeholder="<?=htmlentities($params["placeholder"], 3, "UTF-8") ?>"><?=html($params["value"]); ?></TEXTAREA><script>	CKEDITOR.replace( '<?=$EleId; ?>', {		extraPlugins : 'bbcode',		language: 'de',		//filebrowserUploadUrl : '/uploader/upload.php',        filebrowserImageUploadUrl : '<?=get_path("api/uploader/image.json"); ?>',		scayt_sLang : '<?=SiteConfig::val("language") ?>',		scayt_autoStartup : true,		toolbar:		[			['Source', '-', 'Bold', 'Italic', '-', 'NumberedList', 'BulletedList', '-', 'Image','-', 'Link', 'Unlink'],			['Cut','Copy','Paste','PasteText'],			['Undo','Redo','-','RemoveFormat','Scayt'],			['button-code','youtube','fmath_formula']		]	});</script><style>span#cke_text { padding: 0px !important; }</style>