<?php	echo('<div class="paginator">');	if ($params["page"] > 1) echo('<a href="'.URL::addVar("page", $params["page"]-1).'" class="prev" title="'._e("Previous Page").'">'._e("« Previous").'</a>');		for ($i = max(1,$params["page"]-10); $i <= min($params["page"]+10,$params["max"]); $i++)		echo('<a href="'.URL::addVar("page", $i-1).'" class="page '.($i == $params["page"]?"active":"").'" title="'._e("go to {n}th page", array("n" => $i)).'">'.$i.'</a>');		if ($params["page"] < $params["max"]) echo('<a href="'.URL::addVar("page", $params["page"]+1).'" class="next" title="'._e("Next Page").'">'._e("Next »").'</a>');	echo('</div>');?>