<?php$db = new SQL(0);$info = $db->cmdrow(0, 'SELECT * FROM tag_details WHERE tag = "{0}" LIMIT 0,1', array($params["id"]));PageEngine::html("html_head",array("title" => $params["id"],"index" => isset($info["long_desc"])));PageEngine::html("header");?>	<div id="Content" class="content-wrapper PageUserprofile">		<article class="ContentLeft"><?php PageEngine::html("box_tags_navi", array("tag" => $params["id"])); ?>					<h1 class="search-result-summary"><?=_e("Infos for Tag '{0}'", array(0 => $params["id"])); ?></h1><?php		BBCode::render((isset($info["long_desc"])?$info["long_desc"]:""), true);				if (!isset($info["long_desc"]) OR trim($info["long_desc"]) =="") echo('<p class="noTagDescription">'._e("Dieser Tag hat aktuell noch keine Erklärung. Ab 2500 Karma Punkten kannst Du diesen Artikel erstellen und so anderen Usern helfen oder einen kurzen Einblick in das Thema bieten.").'</p>');				if ((MyUser::isloggedin() AND MyUser::getKarmaPoints() >= 2500) OR MyUser::hasAdminRight()) echo('<a href="'.get_path("/tags/".urlencode($params["id"])."/info/edit").'"><button class="blue" type="button">'._e("edit").'</button></a>');?>				</article>				<aside class="ContentRight">		<div style="text-align: center;"><?php	$count = $db->cmdvalue(0, 'SELECT count(*) FROM question_tags WHERE tag="{0}" LIMIT 0,1', array($params["id"]));	echo('<span style="color: #808185; font-size: 30px; font-weight:bold;">'.number_format($count,0,",",".").'</span>');?><br/><?=_e("questions tagged");?></div><div id="widget-tag-synonyms" class="widget tagsynonyms"> 	<h2 class=""><?=_e("Synonyms"); ?></h2><?php	$db = new SQL(0);	$rows = $db->cmdrows(0, 'SELECT tag1, tag2,explaination FROM tag_synonyms WHERE (tag1 = "{0}" OR tag2 = "{0}") AND count_votes >= 5 ORDER BY count_votes DESC', array($params["id"]));	echo('<div class="tags" style="margin-left: 10px;">');	foreach ($rows as $row) {		if ($row["tag1"] == $params["id"]) $row["tag"] = $row["tag2"]; else $row["tag"] = $row["tag1"];		echo('<a class="tag '.Tag::niceClass($row["tag"]).'" href="'.get_path("/tags/".html($row["tag"])."/synonyms").'" TITLE="'.html($row["explaination"]).'" style="margin-right: 4px;">'.html($row["tag"]).'</a>');	}	echo('</div>');?>		</div><div id="widget-tag-stats" class="widget tag stats"> 	<h2 class=""><?=_e("Stats"); ?></h2>	<table class="StatisticTbl">	<tr><th><?=_e("created:");?></th><td><?=(isset($info["dt_created"])?date("d.m.Y H:i:s", $info["dt_created"]):"---");?></td></tr>	<tr><th><?=_e("edited:");?></th><td><?=(isset($info["dt_edited"])?date("d.m.Y H:i:s", $info["dt_edited"]):"---");?></td></tr>	<tr><th><?=_e("views:");?></th><td><?=(isset($info["count_views"])? $info["count_views"]:0);?></td></tr>	<tr><td>	</table>	</div><div id="widget-tag-stats" class="widget tag stats"> 	<h2 class=""><?=_e("Top Answerers"); ?></h2><?php	$db = new SQL(0);	$rows = $db->cmdrows(0, 'SELECT T2.id, T2.username, T2.email_standard, country, show_country, T2.karma, T2.award_gold, T2.award_silver, T2.award_bronce, sum(T1.count_votes)+count(*) as Rating FROM answers as T1 LEFT JOIN user_list as T2 ON T1.author=T2.id WHERE question IN (SELECT question FROM question_tags WHERE tag="{0}") GROUP BY T2.id ORDER BY Rating DESC LIMIT 0,10', array($params["id"]));	foreach ($rows as $row) {		if (!isset($row["karma"])) continue;		echo('<div class="user" style="position: relative; width:180px; height:32px; overflow: hidden; margin-bottom: 5px;">');		echo('<a href="'.UserProfile::ProfilePermaLink($row["id"], $row["username"]).'"><img class="gravatar" width="32" height="32" src="http://www.gravatar.com/avatar/'.md5(strtolower(trim($row["email_standard"]))).'?s=32&d=mm" title="'.html($row["username"]).'" alt="'._e("{0}'s Gravatar Image").'" style="float: left; margin-right:3px; ">');		//if ($row["is_expert"] == "expert") echo('<img src="'.get_path("/skins/default/images/icons/expert.png").'" TITLE="'._e("Expert for this Subject").'" style="width:8px; height:8px; position: absolute; top:24px; left: 28px;"/>');		echo('<span class="username">'.html($row["username"]).'</span></a>');		if ($row["country"] != "" AND $row["show_country"] == "1") echo('<img class="flag" src="'.get_path("/skins/default/images/flags/16x11/".strtolower($row["country"]).".png").'" alt="'._e(Country::getCountryName($row["country"])).'" title="'._e(Country::getCountryName($row["country"])).'"/>');		echo('<br/>');		echo('<span class="karma counter" title="'._e("User karma of {0}",array($row["username"])).'">'.$row["karma"].'</span>');		if ($row["award_gold"] > 0) echo('<span class="award1" title="'._e("gold medals of {0}",array($row["username"])).'">'.$row["award_gold"].'</span>');		if ($row["award_silver"] > 0) echo('<span class="award2" title="'._e("silver medals of {0}",array($row["username"])).'">'.$row["award_silver"].'</span>');		if ($row["award_bronce"] > 0) echo('<span class="award3" title="'._e("bronce medals of {0}",array($row["username"])).'">'.$row["award_bronce"].'</span>');		echo('</div>');	}?>	</div><div id="widget-tag-stats" class="widget tag stats"> 	<h2 class=""><?=_e("Related Tags"); ?></h2>	<ul class="tags"><?php	$db = new SQL(0);	$rows = $db->cmdrows(0, 'SELECT tag, count(*) as counter FROM question_tags WHERE question IN (SELECT question FROM question_tags WHERE tag="{0}") AND tag != "{0}" GROUP BY tag ORDER BY Counter DESC LIMIT 0,10', array($params["id"]));	foreach ($rows as $row) echo('<li style="list-style: none;"><a class="tag '.Tag::niceClass($row["tag"]).'" href="'.get_path("/tags/".html($row["tag"])."/info").'">'.html($row["tag"]).'</a> x '.$row["counter"].'</li>');?></ul>	</div>				</aside>	</div>	<style></style>			<?phpPageEngine::html("footer");?><script type="text/javascript">$(document).ready(function() {        var s = document.createElement('script');        var t = document.getElementsByTagName('script')[0];        s.type = 'text/javascript';        s.async = true;