m: 5px;">');		echo('<a href="'.UserProfile::ProfilePermaLink($row["id"], $row["username"]).'"><img class="gravatar" width="32" height="32" src="http://www.gravatar.com/avatar/'.md5(strtolower(trim($row["email_standard"]))).'?s=32&d=mm" title="'.html($row["username"]).'" alt="'._e("{0}'s Gravatar Image").'" style="float: left; margin-right:3px; ">');		//if ($row["is_expert"] == "expert") echo('<img src="'.get_path("/skins/default/images/icons/expert.png").'" TITLE="'._e("Expert for this Subject").'" style="width:8px; height:8px; position: absolute; top:24px; left: 28px;"/>');		echo('<span class="username">'.html($row["username"]).'</span></a>');		if ($row["country"] != "" AND $row["show_country"] == "1") echo('<img class="flag" src="'.get_path("/skins/default/images/flags/16x11/".strtolower($row["country"]).".png").'" alt="'._e(Country::getCountryName($row["country"])).'" title="'._e(Country::getCountryName($row["country"])).'"/>');		echo('<br/>');		echo('<span class="karma counter" title="'._e("User karma of {0}",array($row["username"])).'">'.$row["karma"].'</span>');		if ($row["award_gold"] > 0) echo('<span class="award1" title="'._e("gold medals of {0}",array($row["username"])).'">'.$row["award_gold"].'</span>');		if ($row["award_silver"] > 0) echo('<span class="award2" title="'._e("silver medals of {0}",array($row["username"])).'">'.$row["award_silver"].'</span>');		if ($row["award_bronce"] > 0) echo('<span class="award3" title="'._e("bronce medals of {0}",array($row["username"])).'">'.$row["award_bronce"].'</span>');		echo('</div>');	}?>	</div><div id="widget-tag-stats" class="widget tag stats"> 	<h2 class=""><?=_e("Related Tags"); ?></h2>	<ul class="tags"><?php	$db = new SQL(0);	$rows = $db->cmdrows(0, 'SELECT tag, count(*) as counter FROM question_tags WHERE question IN (SELECT question FROM question_tags WHERE tag="{0}") AND tag != "{0}" GROUP BY tag ORDER BY Counter DESC LIMIT 0,10', array($params["id"]));	foreach ($rows as $row) echo('