			<div id="widget-tags" class="widget tags"> 				<h2 class="contributorback"><?=_e("Tags"); ?></h2><ul class="tags"><?php$db = new SQL(0);$rows = $db->cmdrows(0, 'SELECT tag, count(*) as Anzahl FROM question_tags WHERE (tag != "") GROUP BY tag ORDER BY Anzahl DESC LIMIT 0,50');foreach ($rows as $row) {	echo('<li><a class="tag '.Tag::niceClass($row["tag"]).'" href="'.get_path("/tags/".html($row["tag"])."/info").'">'.html($row["tag"]).'</a> x <span class="counter">'.$row["Anzahl"].'</span></li>');}?></ul>			</div>