<?phpclass API_uploader {	public static function image($data) {			//print_r($_FILES); exit(1);			$filename = $_FILES["upload"]["tmp_name"];		$handle = fopen($filename, "r");		$data2 = fread($handle, filesize($filename));		// $data is file data		$pvars   = array('image' => base64_encode($data2), 'key' => "7c72107a27bbb6ab5d9b8ce7b751ef3a");		$curl    = curl_init();		curl_setopt($curl, CURLOPT_URL, 'http://api.imgur.com/2/upload.json');		curl_setopt($curl, CURLOPT_TIMEOUT, 30);		curl_setopt($curl, CURLOPT_POST, 1);		curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);		curl_setopt($curl, CURLOPT_POSTFIELDS, $pvars);		$response = curl_exec($curl);		$json = @json_decode($response,true);		$url = $json["upload"]["links"]["original"];		curl_close ($curl);		?><script>window.parent.CKEDITOR.tools.callFunction("<?=$data["CKEditorFuncNum"];?>", "<?=$url;?>");</script><?php		exit(1);			}}