<?phpclass SocialShare {	public static function FacebookPermaLink($url = null) {		$w = array();		if ($url != null) $w["u"] = $url;		else $w["u"] = "http://".$_SERVER["HTTP_HOST"].$_SERVER["REQUEST_URI"];		return 'http://www.facebook.com/share.php?'.http_build_query($w);		}		public static function TwitterPermaLink($url = null) {		$w = array();		if ($url != null) $w["original_referer"] = $url;		else $w["original_referer"] = "http://".$_SERVER["HTTP_HOST"].$_SERVER["REQUEST_URI"];		$w["source"] = "ASIcms";		$w["text"] = "";		$w["url"] = $w["original_referer"];		$w["via"] = "AndreasKasper";		return 'https://twitter.com/intent/tweet?'.http_build_query($w);	}}