<?php$_ENV["basepath"] = dirname(dirname(dirname(dirname(__FILE__))));if (!isset($_SERVER["REQUEST_URI"])) die("Keine REQUEST-URI vom Apache erhalten!");if (!isset($_SERVER["SCRIPT_NAME"])) die("Keine SCRIPT_NAME vom Apache erhalten!");$_SERVER["REQUEST_URI2"] = substr($_SERVER["REQUEST_URI"],strlen($_SERVER["SCRIPT_NAME"])-10);$p = strpos($_SERVER["REQUEST_URI2"],"?");if (!$p) $_SERVER["REQUEST_URIpure"] = $_SERVER["REQUEST_URI2"]; else $_SERVER["REQUEST_URIpure"] = substr($_SERVER["REQUEST_URI2"],0, $p);include_once($_ENV["basepath"]."/app/code/classes/class.i18n.php");include_once($_ENV["basepath"]."/app/code/classes/class.fcache.php");@session_start();if (isset($_GET["_lang"])) $_SESSION["language"] = $_GET["_lang"];i18n::init(isset($_SESSION["language"]) ? $_SESSION["language"] : "de_DE", "install");switch ($_SERVER["REQUEST_URIpure"]) {	case "/":	case "/index.php":		include($_ENV["basepath"]."/app/design/default/install/index.php"); exit(1);	case "/license":		include($_ENV["basepath"]."/app/design/default/install/license.php"); exit(1);	case "/install":		if (isset($_POST["action"]) && $_POST["action"] == "write_config") { include($_ENV["basepath"]."/app/design/default/install/install_page5.php"); exit(1); }		if (isset($_POST["status"]) && $_POST["status"] == "database_ok") { include($_ENV["basepath"]."/app/design/default/install/install_page4.php"); exit(1); }		if (isset($_POST["action"]) && $_POST["action"] == "check_db") {			if (!mysql_connect ($_POST["mysql_host"].":".$_POST["mysql_port"], $_POST["mysql_username"], $_POST["mysql_password"])) { $messages["db"]["error"][] = "Kein Zugriff auf den MySQL-Server"; include($_ENV["basepath"]."/app/design/default/install/install_page3.php"); exit(1); }			if (!mysql_select_db($_POST["mysql_database"])) mysql_query('CREATE DATABASE `'.$_POST["mysql_database"].'` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci');			if (!mysql_select_db($_POST["mysql_database"])) { $messages["db"]["error"][] = "Die Datenbank kann nicht erstellt werden oder ist nicht erreichbar."; include($_ENV["basepath"]."/app/design/default/install/install_page3.php"); exit(1); }			else { $_POST["status"] = "database_ok"; include("routing.install.php"); exit(1); }		}		if (isset($_POST["status"]) && $_POST["status"] == "server_ok") { include($_ENV["basepath"]."/app/design/default/install/install_page3.php"); exit(1); }		elseif (isset($_POST["license"]) && $_POST["license"] == "accept") { include($_ENV["basepath"]."/app/design/default/install/install_page2.php"); exit(1); }		include($_ENV["basepath"]."/app/design/default/install/install_page1.php"); exit(1);	default:		if (isset($_SERVER["REQUEST_URIpure"]) AND isset($_SERVER["REQUEST_URI"])) { header("Location: http://".$_SERVER["HTTP_HOST"].substr($_SERVER["REQUEST_URI"],0,strlen($_SERVER["REQUEST_URI"])-strlen($_SERVER["REQUEST_URIpure"])+1)); exit(1);}		include($_ENV["basepath"]."/app/design/default/install/index.php"); exit(1);}print_r($_SERVER["REQUEST_URIpure"]);exit(1);