<div class="debugging" style="font-family: Courier; font-size:12px; margin-bottom: 50px;"><table><?php$start = PageEngine::$_debuglog["html"][0]["timestamp"];foreach (PageEngine::$_debuglog["html"] as $row) {	echo('<tr><td title="'.html($row["file"]).'">'.$row["page"].'</td>');	echo('<td>'.($row["timestamp"]-$start).'</td>');	echo('</tr>');}	echo('</table>');	echo('<br/>');	echo(format_byte(memory_get_usage(),1)."/".format_byte(memory_get_peak_usage(),1).'<br/>');	echo(SQL::$counter.' Datenbankanfragen<br/>');	echo('</div>');				function format_byte($value, $dec = 1) {    $symbol = array('B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB');    $exp = 0;    $converted_value = 0;    if( $value > 0 ) {      $exp = floor( log($value)/log(1024) );      $converted_value = ( $value/pow(1024,floor($exp)) );    }    return number_format($converted_value,$dec,",",".")." ".$symbol[$exp];  }