<?php$rss = new RSS("Fragen - ".SiteConfig::val("page/title","Q/A Forum"), "Die neusten Fragen aus dem Forum", get_path("/questions/rss"));$db = new SQL(0);$rows = $db->cmdrows(0, 'SELECT T1.*,T2.username as authorname FROM questions as T1 LEFT JOIN user_list as T2 ON T1.author=T2.id WHERE 1 ORDER BY date_created DESC LIMIT 0,30');foreach ($rows as $row) {	$item = new RSSItem($row["title"], $row["question"].'<br/><br/><div style="color:#8080ff;"><i>'.html($row["authorname"]).'</i></div>', Question::PermalinkByData($row["id"], $row["title"]));	$rss->addItem($item);	}//die($rss->fetch());$rss->display();exit(1);