			<div id="contrib-users" class="widget"> 				<h2 class="contributorback"><?=_e("Contributors"); ?></h2><?php$db = new SQL(0);$rows = $db->cmdrows(0, 'SELECT T1.id, T1.username ,T1.email_standard as email ,T2.last_writeactionFROM user_list as T1 LEFT JOIN user_action as T2 ON T1.id=T2.userWHERE 1 GROUP BY T1.id ORDER BY T2.last_writeaction DESC LIMIT 0,16');foreach ($rows as $row) {	echo('<a style="text-decoration:none" href="'.UserProfile::ProfilePermaLink($row["id"], $row["username"]).'"><img class="gravatar" width="48" height="48" src="http://www.gravatar.com/avatar/'.md5(strtolower(trim($row["email"]))).'?s=48&d=mm" title="'.html($row["username"]).'" alt="'._e("{0}'s Gravatar Image",array($row["username"])).'"></a>');}?>			</div>