<?php$db = new SQL(0);$rows = $db->cmdrows(0, 'SELECT id, title,MATCH (`title`,`question`,`tags`) AGAINST ("{0}") as Score FROM questions WHERE id != {1} AND MATCH (`title`,`question`,`tags`) AGAINST ("{0}") ORDER BY Score DESC LIMIT 0,5', array($params["data"]["title"]." ".$params["data"]["question"]." ".$params["data"]["tags"], $params["data"]["id"]+0));?><div id="widget-similar" class="widget similar questions"> 				<h2 class=""><?=_e("similar questions"); ?></h2><ul><?php	foreach ($rows as $row) echo('<li><a class="question" href="'.Question::PermalinkByData($row["id"], $row["title"]).'">'.html($row["title"]).'</a> '.number_format($row["Score"],1).'%</li>');?></ul>			</div><style>#widget-similar ul { padding:0px; margin:0px; }#widget-similar li { font-family: 'Yanone Kaffeesatz',Arial,sans-serif;  font-size:14px; list-style: none; margin: 4px 0px; padding:0px; color: #707070; }#widget-similar a { color: #1B79BD; }#widget-similar a:hover { }</Style>